import React from "react";
import Popup from "reactjs-popup";
import { TextField} from '@material-ui/core';
import './profile2.css';
import {
  StyleSheet,
  Text,
  View,
  Image,
  TouchableOpacity,
  ScrollView
} from 'react-native';

 
function ModalModify(){
  return(
  <Popup trigger={<TouchableOpacity style={styles.buttonContainer} activeOpacity={.7}>
                      <Text>Edit Profile</Text>  
                  </TouchableOpacity>
    } modal>
    {close => (
      <div className="modal">
        <View style={styles.header}><Text style={styles.title}>My Profile</Text></View>
        <div className="content">
        <View style={styles.popupContent}>
            <ScrollView contentContainerStyle={styles.modalInfo}>
                <Image style={styles.image} source={{uri: 'https://bootdey.com/img/Content/avatar/avatar2.png'}}/>
                <hr/>
                <form noValidate autoComplete="off" className="form">
                <TextField
                    disabled
                    id="name"
                    label="Name"                  
                    margin="normal"
                    onChange={this.handleNameChange}
                    value={this.state.name}
                    defaultValue={this.state.name}
                >
                </TextField>
                <br></br>
                <TextField
                    id="email"
                    label="Email"                  
                    margin="normal"
                    onChange={this.handleEmailChange}
                    value={this.state.email}
                    defaultValue={this.state.email}
                >
                </TextField>
                <br></br>
                <TextField
                    id="city"
                    label="City"                  
                    margin="normal"
                    onChange={this.handleCityChange}
                    value={this.state.city}
                    defaultValue={this.state.city}
                >
                </TextField>            
                </form>
                </ScrollView>
        </View>
        </div>
        <View style={styles.popupButtons}>
        <div className="actions">
          <TouchableOpacity style={styles.btnSave} activeOpacity={.7} onClick={() => {close();}}>
            <Text>Save</Text>  
          </TouchableOpacity>
          
          <TouchableOpacity style={styles.btnClose} activeOpacity={.7} onClick={() => {close();}}>
              <Text>Close</Text>  
          </TouchableOpacity>
        </div>
        </View>
      </div>
    )}
  </Popup>
  )}
  
  const styles = StyleSheet.create({
    image:{
      width:90,
      height:90,
      borderRadius:45,
    },
    email:{
      marginHorizontal:10,
      fontSize:22,
    },
    name:{
      fontSize:22,
      color:"#FFFFFF",
      fontWeight:'600',
    },
    buttonContainer: {
      marginTop:10,
      height:45,
      flexDirection: 'row',
      justifyContent: 'center',
      alignItems: 'center',
      marginBottom:20,
      width:250,
      borderRadius:30,
      backgroundColor: "#00CED1",
    },
    popupContent: {
      //alignItems: 'center',
      marginTop: 30,
      margin: 5,
      height:250,
      flex: 2,
    },
    popupButtons: {
      marginTop: 15,
      flexDirection: 'row',
      borderTopWidth: 1,
      borderColor: "#eee",
      justifyContent:'center'
    },
    title:{
      fontSize:24,
      color: "#00CED1",
      marginBottom:10,
    },
    header: {
      marginTop: 10,
      flexDirection: 'row',
      borderBottomWidth: 1,
      borderColor: "#eee",
      justifyContent:'center'
    },
    btnClose:{
      marginTop:10,
      height:40,
      flexDirection: 'row',
      justifyContent: 'center',
      alignItems: 'center',
      marginBottom:20,
      width:250,
      borderRadius:30,
      backgroundColor: "#cb3234",
    },
    btnSave:{
      marginTop:10,
      height:40,
      flexDirection: 'row',
      justifyContent: 'center',
      alignItems: 'center',
      marginBottom:20,
      width:250,
      borderRadius:30,
      backgroundColor: "#07d257",
    },
    modalInfo:{
      alignItems:'center',
      justifyContent:'center',
    }
  });
   

  export default ModalModify;
