function TripBadge(trips) {
  if (trips < 10) {
    return ("Router Baby");
  } else if (trips < 50) {
    return ("Router Junior");
  } else if (trips < 100) {
    return ("Router Senior");
  } else {
    return ("Router Pro");
  }
}

function DistanceBadge(km) {
  if (km < 20) {
    return ("Baby Biker");
  } else if (km < 100) {
    return ("Junior Biker");
  } else if (km < 250) {
    return ("Senior Biker");
  } else {
    return ("Pro Biker");
  }
}
